import React, { useEffect, useState, useRef } from 'react';
import * as joint from '@joint/core';
import { v4 as uuidv4 } from 'uuid';

function RoadmapView() {
  const paperContainer = useRef(null);
  const graph = useRef(null);
  const paper = useRef(null);
  const [roadmapData, setRoadmapData] = useState(() => {
    const savedData = localStorage.getItem('roadmapData');
    return savedData ? JSON.parse(savedData) : { subjects: [] };
  });
  const [newSubject, setNewSubject] = useState('');
  const [newTopic, setNewTopic] = useState('');
  const [selectedSubjectId, setSelectedSubjectId] = useState('');
  const [completionPercentage, setCompletionPercentage] = useState(0);

  // Initialize JointJS graph and paper
  useEffect(() => {
    graph.current = new joint.dia.Graph();
    paper.current = new joint.dia.Paper({
      el: paperContainer.current,
      model: graph.current,
      width: 800, // Fixed width
      height: 600,
      gridSize: 1,
      interactive: false,
    });

    renderDiagram();
    calculateCompletionPercentage();
  }, []);

  // Render the diagram with auto-layout
  const renderDiagram = () => {
    graph.current.clear();

    const elements = [];
    const links = [];

    roadmapData.subjects.forEach((subject, subjectIndex) => {
      const completedCount = subject.topics.filter(topic => topic.completed).length;
      const totalCount = subject.topics.length;
      const progress = totalCount ? (completedCount / totalCount) * 100 : 0; // Calculate progress

      const subjectElement = new joint.shapes.standard.Rectangle();
      subjectElement.position(100, 100 + subjectIndex * 150);
      subjectElement.resize(100, 40);
      subjectElement.attr({
        body: { fill: 'lightblue' },
        label: { text: subject.name, fill: 'black' },
      });

      subjectElement.addTo(graph.current);
      elements.push(subjectElement);

      // Create progress bar inside the subject element
      const progressBar = new joint.shapes.standard.Rectangle();
      progressBar.position(100, 130 + subjectIndex * 150);
      progressBar.resize(progress, 10); // Set width based on progress
      progressBar.attr({
        body: { fill: 'green' },
      });

      progressBar.addTo(graph.current);
      elements.push(progressBar);

      // Create topics and connect them to the subject
      let currentY = 150 + subjectIndex * 150; // Start below subject
      subject.topics.forEach((topic) => {
        const topicElement = new joint.shapes.standard.Rectangle();
        topicElement.position(300, currentY);
        topicElement.resize(100, 40);
        topicElement.attr({
          body: { fill: topic.completed ? 'lightgreen' : 'lightcoral' },
          label: { text: topic.name, fill: 'black' },
        });

        topicElement.addTo(graph.current);
        elements.push(topicElement);

        const link = new joint.shapes.standard.Link();
        link.source(subjectElement);
        link.target(topicElement);
        link.addTo(graph.current);
        links.push(link);

        // Update currentY for the next topic
        currentY += 60; // Space between topics
      });
    });

    // Check if layout is defined and apply auto-layout
    if (joint.layout && joint.layout.DirectedGraph) {
      joint.layout.DirectedGraph.layout(graph.current, {
        setNodePos: (node, x, y) => {
          node.position(x, y);
        },
      });
    }
  };

  // Toggle topic completion status
  const handleTopicCompletionToggle = (subjectId, topicId) => {
    const updatedRoadmapData = { ...roadmapData };
    updatedRoadmapData.subjects.forEach((subject) => {
      if (subject.id === subjectId) {
        const topicToUpdate = subject.topics.find((t) => t.id === topicId);
        if (topicToUpdate) {
          topicToUpdate.completed = !topicToUpdate.completed;
        }
      }
    });

    setRoadmapData(updatedRoadmapData);
    calculateCompletionPercentage();
    saveToLocalStorage(updatedRoadmapData); // Save changes to local storage
  };

  // Calculate the percentage of completed topics
  const calculateCompletionPercentage = () => {
    const completedTopics = roadmapData.subjects.reduce((total, subject) => {
      return total + subject.topics.filter(topic => topic.completed).length;
    }, 0);
    const totalTopics = roadmapData.subjects.reduce((total, subject) => {
      return total + subject.topics.length;
    }, 0);

    setCompletionPercentage((completedTopics / totalTopics) * 100 || 0);
  };

  // Add a new subject
  const addSubject = () => {
    if (newSubject.trim() !== '') {
      const updatedRoadmapData = {
        ...roadmapData,
        subjects: [
          ...roadmapData.subjects,
          {
            id: uuidv4(),
            name: newSubject,
            topics: [],
            collapsed: false, // Add collapsed state
          },
        ],
      };
      setRoadmapData(updatedRoadmapData);
      setNewSubject('');
      saveToLocalStorage(updatedRoadmapData); // Save changes to local storage
    } else {
      alert('Please enter a subject name.');
    }
  };

  // Add a new topic to a selected subject
  const addTopic = () => {
    if (newTopic.trim() !== '' && selectedSubjectId) {
      const updatedRoadmapData = { ...roadmapData };
      const subjectToUpdate = updatedRoadmapData.subjects.find(
        (subject) => subject.id === selectedSubjectId
      );
      if (subjectToUpdate) {
        subjectToUpdate.topics.push({
          id: uuidv4(),
          name: newTopic,
          completed: false,
        });
        setRoadmapData(updatedRoadmapData);
        setNewTopic('');
        saveToLocalStorage(updatedRoadmapData); // Save changes to local storage
      } else {
        alert('Subject not found.');
      }
    } else {
      alert('Please enter a topic name and select a subject.');
    }
  };

  // Save roadmap data to local storage
  const saveToLocalStorage = (data) => {
    localStorage.setItem('roadmapData', JSON.stringify(data));
  };

  // Re-render diagram whenever roadmap data changes
  useEffect(() => {
    if (graph.current) {
      renderDiagram();
    }
  }, [roadmapData]);

  return (
    <div>
      <div>
        <h3>Add New Subject</h3>
        <input
          type="text"
          value={newSubject}
          onChange={(e) => setNewSubject(e.target.value)}
          placeholder="Subject Name"
        />
        <button onClick={addSubject}>Add Subject</button>
      </div>

      <div>
        <h3>Add New Topic</h3>
        <select
          onChange={(e) => setSelectedSubjectId(e.target.value)}
          value={selectedSubjectId}
        >
          <option value="">Select Subject</option>
          {roadmapData.subjects.map((subject) => (
            <option key={subject.id} value={subject.id}>
              {subject.name}
            </option>
          ))}
        </select>
        <input
          type="text"
          value={newTopic}
          onChange={(e) => setNewTopic(e.target.value)}
          placeholder="Topic Name"
        />
        <button onClick={addTopic}>Add Topic</button>
      </div>

      <div ref={paperContainer} style={{ border: '1px solid black', width: '800px', height: '600px' }} />

      <h3>Topics Completion</h3>
      <ul>
        {roadmapData.subjects.map((subject) => (
          <li key={subject.id}>
            <h4>{subject.name}</h4>
            <ul>
              {subject.topics.map((topic) => (
                <li key={topic.id}>
                  <label>
                    <input
                      type="checkbox"
                      checked={topic.completed}
                      onChange={() => handleTopicCompletionToggle(subject.id, topic.id)}
                    />
                    {topic.completed && <span> âœ… </span>}
                    {topic.name}
                  </label>
                </li>
              ))}
            </ul>
          </li>
        ))}
      </ul>

      <p>Overall Completion: {completionPercentage.toFixed(2)}%</p>
    </div>
  );
}

export default RoadmapView;
