/* global mermaid */ // Tell ESLint about the mermaid global variable

import React, { useRef, useEffect, useState } from 'react';
import { roadmapData as initialRoadmapData } from '../data/roadmapData'; 

function RoadmapView() {
  const mermaidContainer = useRef(null);
  const [roadmapData, setRoadmapData] = useState(initialRoadmapData);
  const [completionPercentage, setCompletionPercentage] = useState(0); 

  useEffect(() => {
    if (mermaidContainer.current) {
      renderMermaidDiagram(); 
      calculateCompletionPercentage(); 
    }
  }, []);

  const renderMermaidDiagram = () => {
    let mermaidMarkdown = 'graph LR\n';

    roadmapData.subjects.forEach((subject) => {
      mermaidMarkdown += `${subject.id}([${subject.name}])\n`;

      subject.topics.forEach((topic) => {
        mermaidMarkdown += `${subject.id} --> ${topic.id}[${topic.name}]:::clickable onClick handleTopicClick("${topic.id}")\n`;  
      });
    });

    mermaidContainer.current.innerHTML = mermaidMarkdown;
    mermaid.init(); 
  };

  // Define handleTopicClick in the global scope
  window.handleTopicClick = (topicId) => {
    const updatedRoadmapData = { ...roadmapData };
    updatedRoadmapData.subjects.forEach((subject) => {
      const topicToUpdate = subject.topics.find((t) => t.id === topicId);
      if (topicToUpdate) {
        topicToUpdate.completed = !topicToUpdate.completed;
      }
    });

    setRoadmapData(updatedRoadmapData); 
    calculateCompletionPercentage(); 
  };

  const calculateCompletionPercentage = () => {
    const completedTopics = roadmapData.subjects.reduce((total, subject) => {
      return total + subject.topics.filter(topic => topic.completed).length;
    }, 0);
    const totalTopics = roadmapData.subjects.reduce((total, subject) => {
      return total + subject.topics.length;
    }, 0);

    setCompletionPercentage((completedTopics / totalTopics) * 100);
  };

  useEffect(() => {
    renderMermaidDiagram();
  }, [roadmapData]); 

  return (
    <div>
      <div ref={mermaidContainer} className="mermaid" />
      <p>Roadmap Completion: {completionPercentage}%</p>
    </div>
  );
}

export default RoadmapView;
