// src/components/RoadmapView.js
import React, { useRef, useEffect, useState } from 'react';

function RoadmapView() {
  const mermaidContainer = useRef(null);

  useEffect(() => {
    if (mermaidContainer.current) {
      const mermaidMarkdown = `
flowchart TD
    A[Christmas] -->|Get money| B(Go shopping)
    B --> C{Let me think}
    C -->|One| D[Laptop]
    C -->|Two| E[iPhone]
    C -->|Three| F[fa:fa-car Car]
      `;

      mermaidContainer.current.innerHTML = mermaidMarkdown; 
    }
  }, []); 

  return (
    <div>
      <div ref={mermaidContainer} className="mermaid" /> 
    </div>
  );
}

export default RoadmapView;
