import React, { useEffect, useState, useRef } from 'react';
import * as joint from '@joint/core';
import { v4 as uuidv4 } from 'uuid';
import './RoadmapView.css';

function RoadmapView() {
  const paperContainer = useRef(null);
  const graph = useRef(null);
  const [roadmapData, setRoadmapData] = useState({ subjects: [] });
  const [newSubject, setNewSubject] = useState('');
  const [newTopic, setNewTopic] = useState('');
  const [selectedSubjectId, setSelectedSubjectId] = useState('');
  const [completionPercentage, setCompletionPercentage] = useState(0);

  useEffect(() => {
    graph.current = new joint.dia.Graph();
    const paper = new joint.dia.Paper({
      el: paperContainer.current,
      model: graph.current,
      width: 800,
      height: 600,
      gridSize: 1,
      interactive: { elementMove: false },
    });

    renderDiagram();
    calculateCompletionPercentage();

    return () => {
      graph.current.off('element:pointerdown');
    };
  }, []);

  const renderDiagram = () => {
    // ... (Existing renderDiagram implementation from your code)
  };

  const calculateCompletionPercentage = () => {
    // ... (Existing calculateCompletionPercentage implementation)

  };

  const addSubject = () => {
    // ... (Existing addSubject implementation)
  };

  const deleteSubject = (subjectId) => {
    // ... (Existing deleteSubject implementation)
  };

  const addTopic = () => {
     // ... (Existing addTopic implementation)
  };

  const markAsCompleted = (topicId) => {
    // ... (Existing markAsCompleted implementation)
  };

  const editTopic = (topicId) => {
    // ... (Existing editTopic implementation)

  };

  const handleFileUpload = (event, topicId) => {
    const file = event.target.files[0];
    if (file) {
      const updatedRoadmapData = { ...roadmapData };
      updatedRoadmapData.subjects.forEach((subject) => {
        const topic = subject.topics.find((t) => t.id === topicId);
        if (topic) {
          topic.uploadedFile = file.name;  // Store the filename (simulated upload)
        }
      });
      setRoadmapData(updatedRoadmapData);
      alert(`File "${file.name}" uploaded for topic "${topic?.name}" (simulated)`); // Provide feedback
    }
  };

  const deleteFile = (topicId) => {
    const updatedRoadmapData = { ...roadmapData };
    updatedRoadmapData.subjects.forEach((subject) => {
      const topic = subject.topics.find((t) => t.id === topicId);
      if (topic && topic.uploadedFile) {
        delete topic.uploadedFile;
        setRoadmapData(updatedRoadmapData);
        alert("File deleted (simulated)"); // Provide feedback
      }
    });
  };


  const deleteTopic = (topicId) => {
    // ... (Existing deleteTopic implementation)

  };

  useEffect(() => {
    if (graph.current) {
      renderDiagram();
    }
  }, [roadmapData]);

  return (
    <div className="roadmap-container">

      {/* ... (Your existing JSX for Subject and Topic input) */}

      <div ref={paperContainer} className="diagram-container" />

      <div className="manage-container">
        <h3>Manage Topics</h3>
        {selectedSubjectId && (
          <ul>
            {roadmapData.subjects.find((subject) => subject.id === selectedSubjectId)?.topics.map((topic) => (
              <li key={topic.id} className="topic-item">
                <span className={topic.completed ? 'completed' : ''}>{topic.name}</span>

                <div className="topic-actions">
                   <input type="file" onChange={(e) => handleFileUpload(e, topic.id)} />
                  {topic.uploadedFile && (
                    <div>
                      <span>File: {topic.uploadedFile}</span>
                      <button onClick={() => deleteFile(topic.id)} className="action-button">Delete File</button>
                    </div>
                  )}
                  {/* ... Other action buttons (mark complete, edit, delete topic) */}
                   <button onClick={() => markAsCompleted(topic.id)} className="action-button">Mark as {topic.completed ? "Incomplete" : "Completed"}</button>
                   <button onClick={() => editTopic(topic.id)} className="action-button">Edit</button>
                   <button onClick={() => deleteTopic(topic.id)} className="action-button">Delete</button>

                </div>
              </li>
            ))}
          </ul>
        )}
      </div>

       {/* ... (Your existing JSX for Completion Percentage) */}
    </div>
  );
}

export default RoadmapView;
