import React, { useState, useEffect, useRef } from 'react';
import { dia } from 'jointjs'; // Import Graph and Paper from dia namespace
import 'jointjs/dist/joint.css';
import './RoadmapView.css'; // Assuming you have some custom styles

const { Graph, Paper } = dia;

function RoadmapView() {
    const graphRef = useRef(null);
    const paperRef = useRef(null);
    const [graph, setGraph] = useState(null);
    const [paper, setPaper] = useState(null);
    const [subjects, setSubjects] = useState([
        { id: 'subject1', name: 'Math', topics: [{ id: 'topic1', name: 'Algebra', completed: false }] },
        { id: 'subject2', name: 'Science', topics: [{ id: 'topic2', name: 'Physics', completed: false }] }
    ]);
    const [selectedTopic, setSelectedTopic] = useState(null);

    useEffect(() => {
        const g = new Graph();
        setGraph(g);

        const p = new Paper({
            el: paperRef.current,
            model: g,
            width: 800,
            height: 600,
            gridSize: 10,
            drawGrid: true,
            interactive: false // Disable interaction
        });
        setPaper(p);

        return () => {
            p.remove();
            g.clear();
        };
    }, []);

    useEffect(() => {
        if (graph) {
            renderDiagram();
        }
    }, [graph, subjects]);

    const renderDiagram = () => {
        graph.clear(); // Clear previous diagram

        subjects.forEach((subject, subjectIdx) => {
            const subjectElement = new dia.Element({
                position: { x: 50 + subjectIdx * 200, y: 50 },
                size: { width: 100, height: 40 },
                attrs: { text: { text: subject.name, fill: 'black' }, rect: { fill: '#f9f9f9', stroke: 'black' } }
            });
            graph.addCell(subjectElement);

            subject.topics.forEach((topic, topicIdx) => {
                const topicElement = new dia.Element({
                    position: { x: 50 + subjectIdx * 200, y: 100 + topicIdx * 60 },
                    size: { width: 100, height: 40 },
                    attrs: { text: { text: topic.name, fill: 'black' }, rect: { fill: topic.completed ? '#8bc34a' : '#ff5252', stroke: 'black' } }
                });

                graph.addCell(topicElement);
                const link = new dia.Link({
                    source: { id: subjectElement.id },
                    target: { id: topicElement.id },
                    attrs: { '.connection': { stroke: 'black' } }
                });
                graph.addCell(link);

                // Handle topic click for marking as completed
                topicElement.on('element:pointerclick', () => handleTopicClick(subject.id, topic.id));
            });
        });
    };

    const handleTopicClick = (subjectId, topicId) => {
        const updatedSubjects = subjects.map((subject) => {
            if (subject.id === subjectId) {
                return {
                    ...subject,
                    topics: subject.topics.map((topic) =>
                        topic.id === topicId ? { ...topic, completed: !topic.completed } : topic
                    )
                };
            }
            return subject;
        });

        setSubjects(updatedSubjects);
    };

    const handleSubjectChange = (subjectId, newName) => {
        const updatedSubjects = subjects.map((subject) =>
            subject.id === subjectId ? { ...subject, name: newName } : subject
        );
        setSubjects(updatedSubjects);
    };

    const handleTopicChange = (subjectId, topicId, newName) => {
        const updatedSubjects = subjects.map((subject) => {
            if (subject.id === subjectId) {
                return {
                    ...subject,
                    topics: subject.topics.map((topic) =>
                        topic.id === topicId ? { ...topic, name: newName } : topic
                    )
                };
            }
            return subject;
        });

        setSubjects(updatedSubjects);
    };

    const handleDeleteTopic = (subjectId, topicId) => {
        const updatedSubjects = subjects.map((subject) => {
            if (subject.id === subjectId) {
                return {
                    ...subject,
                    topics: subject.topics.filter((topic) => topic.id !== topicId)
                };
            }
            return subject;
        });

        setSubjects(updatedSubjects);
    };

    return (
        <div className="roadmap-container">
            <div ref={paperRef} className="roadmap-paper" />
            <div className="roadmap-sidebar">
                {selectedTopic && (
                    <div className="topic-details">
                        <h3>Edit Topic</h3>
                        <input
                            type="text"
                            value={selectedTopic.name}
                            onChange={(e) => handleTopicChange(selectedTopic.subjectId, selectedTopic.id, e.target.value)}
                        />
                        <button onClick={() => handleDeleteTopic(selectedTopic.subjectId, selectedTopic.id)}>
                            Delete Topic
                        </button>
                    </div>
                )}
            </div>
        </div>
    );
}

export default RoadmapView;
