// src/components/RoadmapView.js
import React, { useRef, useEffect, useState } from 'react';
import { roadmapData } from '../data/roadmapData';

function RoadmapView() {
  const mermaidContainer = useRef(null);

  useEffect(() => {
    if (mermaidContainer.current) {
      // Generate Mermaid.js flowchart markdown from roadmapData
      let mermaidMarkdown = 'flowchart TD\n';

      roadmapData.subjects.forEach((subject) => {
        mermaidMarkdown += `  subgraph ${subject.name}\n`; 

        subject.topics.forEach((topic) => {
          mermaidMarkdown += `    ${topic.id}[${topic.name}]\n`;
        });

        mermaidMarkdown += '  end\n'; 
      });

      mermaidContainer.current.innerHTML = mermaidMarkdown;
    }
  }, []);

  return (
    <div>
      <div ref={mermaidContainer} className="mermaid" /> 
    </div>
  );
}

export default RoadmapView;
