import React, { useEffect, useState, useRef } from 'react';
import * as joint from '@joint/core';
import { v4 as uuidv4 } from 'uuid';

function RoadmapView() {
  const paperContainer = useRef(null);
  const graph = useRef(null);
  const [roadmapData, setRoadmapData] = useState({ subjects: [] });
  const [newSubject, setNewSubject] = useState('');
  const [newTopic, setNewTopic] = useState('');
  const [selectedSubjectId, setSelectedSubjectId] = useState('');
  const [completionPercentage, setCompletionPercentage] = useState(0);

  useEffect(() => {
    graph.current = new joint.dia.Graph();
    const paper = new joint.dia.Paper({
      el: paperContainer.current,
      model: graph.current,
      width: 800,
      height: 600,
      gridSize: 1,
      interactive: { elementMove: false }, // Disable dragging
    });

    renderDiagram();
    calculateCompletionPercentage();

    return () => {
      graph.current.off('element:pointerdown'); // Clean up
    };
  }, []);

  const renderDiagram = () => {
    graph.current.clear();
    roadmapData.subjects.forEach((subject, subjectIndex) => {
      const subjectElement = new joint.shapes.standard.Rectangle();
      subjectElement.position(100, 100 + subjectIndex * 150);
      subjectElement.resize(100, 40);
      subjectElement.attr({
        body: { fill: 'lightblue' },
        label: { text: subject.name, fill: 'black' },
      });

      subjectElement.attr('element', { draggable: false });
      subjectElement.addTo(graph.current);

      let currentY = 150 + subjectIndex * 150;
      subject.topics.forEach((topic, topicIndex) => {
        const topicElement = new joint.shapes.standard.Rectangle();
        topicElement.position(300, currentY);
        topicElement.resize(100, 40);
        topicElement.attr({
          body: { fill: topic.completed ? 'lightgreen' : 'lightcoral' },
          label: { text: topic.name, fill: 'black' },
        });

        topicElement.addTo(graph.current);
        topicElement.attr('element', { draggable: false });

        const link = new joint.shapes.standard.Link();
        if (topicIndex === 0) {
          // Connect first topic to the subject
          link.source(subjectElement);
        } else {
          // Connect subsequent topics to the previous topic
          const previousTopicElement = graph.current.getCell(roadmapData.subjects[subjectIndex].topics[topicIndex - 1].id);
          link.source(previousTopicElement);
        }
        link.target(topicElement);
        link.addTo(graph.current);

        currentY += 60; // Space between topics
      });
    });
  };

  const calculateCompletionPercentage = () => {
    const completedTopics = roadmapData.subjects.reduce((total, subject) => {
      return total + subject.topics.filter(topic => topic.completed).length;
    }, 0);
    const totalTopics = roadmapData.subjects.reduce((total, subject) => {
      return total + subject.topics.length;
    }, 0);
    setCompletionPercentage((completedTopics / totalTopics) * 100 || 0);
  };

  const addSubject = () => {
    if (newSubject.trim() !== '') {
      const updatedRoadmapData = {
        ...roadmapData,
        subjects: [
          ...roadmapData.subjects,
          {
            id: uuidv4(),
            name: newSubject,
            topics: [],
          },
        ],
      };
      setRoadmapData(updatedRoadmapData);
      setNewSubject('');
    } else {
      alert('Please enter a subject name.');
    }
  };

  const deleteSubject = (subjectId) => {
    const updatedRoadmapData = {
      ...roadmapData,
      subjects: roadmapData.subjects.filter(subject => subject.id !== subjectId),
    };
    setRoadmapData(updatedRoadmapData);
    setSelectedSubjectId(''); // Clear the selected subject
  };

  const addTopic = () => {
    if (newTopic.trim() !== '' && selectedSubjectId) {
      const updatedRoadmapData = { ...roadmapData };
      const subjectToUpdate = updatedRoadmapData.subjects.find(
        (subject) => subject.id === selectedSubjectId
      );
      if (subjectToUpdate) {
        const topicId = uuidv4();
        subjectToUpdate.topics.push({
          id: topicId,
          name: newTopic,
          completed: false,
        });
        setRoadmapData(updatedRoadmapData);
        setNewTopic('');
        renderDiagram(); // Re-render diagram to reflect the changes
      } else {
        alert('Subject not found.');
      }
    } else {
      alert('Please enter a topic name and select a subject.');
    }
  };

  const markAsCompleted = (topicId) => {
    const updatedRoadmapData = { ...roadmapData };
    updatedRoadmapData.subjects.forEach((subject) => {
      if (subject.id === selectedSubjectId) {
        const topicToUpdate = subject.topics.find((t) => t.id === topicId);
        if (topicToUpdate) {
          topicToUpdate.completed = !topicToUpdate.completed;
        }
      }
    });
    setRoadmapData(updatedRoadmapData);
    calculateCompletionPercentage();
  };

  const editTopic = (topicId) => {
    const updatedRoadmapData = { ...roadmapData };
    updatedRoadmapData.subjects.forEach((subject) => {
      if (subject.id === selectedSubjectId) {
        const topicToUpdate = subject.topics.find((t) => t.id === topicId);
        if (topicToUpdate) {
          const newName = prompt('Edit topic name:', topicToUpdate.name);
          if (newName && newName.trim() !== '') {
            topicToUpdate.name = newName.trim();
          }
        }
      }
    });
    setRoadmapData(updatedRoadmapData);
  };

  const deleteTopic = (topicId) => {
    const updatedRoadmapData = { ...roadmapData };
    updatedRoadmapData.subjects.forEach((subject) => {
      if (subject.id === selectedSubjectId) {
        subject.topics = subject.topics.filter((t) => t.id !== topicId);
      }
    });
    setRoadmapData(updatedRoadmapData);
    calculateCompletionPercentage();
  };

  useEffect(() => {
    if (graph.current) {
      renderDiagram();
    }
  }, [roadmapData]);

  return (
    <div style={{ display: 'flex' }}>
      <div>
        <h3>Add New Subject</h3>
        <input
          type="text"
          value={newSubject}
          onChange={(e) => setNewSubject(e.target.value)}
          placeholder="Subject Name"
        />
        <button onClick={addSubject}>Add Subject</button>
        <h3>Existing Subjects</h3>
        <ul>
          {roadmapData.subjects.map((subject) => (
            <li key={subject.id}>
              {subject.name}
              <button onClick={() => deleteSubject(subject.id)} style={{ marginLeft: '10px' }}>Delete</button>
            </li>
          ))}
        </ul>
      </div>

      <div>
        <h3>Add New Topic</h3>
        <select
          onChange={(e) => setSelectedSubjectId(e.target.value)}
          value={selectedSubjectId}
        >
          <option value="">Select Subject</option>
          {roadmapData.subjects.map((subject) => (
            <option key={subject.id} value={subject.id}>
              {subject.name}
            </option>
          ))}
        </select>
        <input
          type="text"
          value={newTopic}
          onChange={(e) => setNewTopic(e.target.value)}
          placeholder="Topic Name"
        />
        <button onClick={addTopic}>Add Topic</button>
      </div>

      <div ref={paperContainer} style={{ border: '1px solid black', width: '800px', height: '600px' }} />
      
      {/* Right side for managing topics */}
      <div style={{ marginLeft: '20px' }}>
        <h3>Manage Topics</h3>
        {selectedSubjectId && (
          <ul>
            {roadmapData.subjects.find((subject) => subject.id === selectedSubjectId)?.topics.map((topic) => (
              <li key={topic.id} style={{ marginBottom: '10px' }}>
                <span style={{ textDecoration: topic.completed ? 'line-through' : 'none' }}>
                  {topic.name}
                </span>
                <div>
                  <button onClick={() => markAsCompleted(topic.id)}>
                    Mark as {topic.completed ? 'Incomplete' : 'Completed'}
                  </button>
                  <button onClick={() => editTopic(topic.id)}>Edit</button>
                  <button onClick={() => deleteTopic(topic.id)}>Delete</button>
                </div>
              </li>
            ))}
          </ul>
        )}
        {!selectedSubjectId && <p>Please select a subject to manage topics.</p>}
      </div>

      <p>Overall Completion: {completionPercentage.toFixed(2)}%</p>
    </div>
  );
}

export default RoadmapView;
