import React, { useRef, useEffect, useState } from 'react';
import { v4 as uuidv4 } from 'uuid';
import { Graph, Paper } from '@jointjs/core';

const initialRoadmapData = {
  subjects: [
    { id: uuidv4(), name: 'Math', topics: [{ id: uuidv4(), name: 'Algebra', completed: false }, { id: uuidv4(), name: 'Geometry', completed: false }] },
    { id: uuidv4(), name: 'Science', topics: [{ id: uuidv4(), name: 'Physics', completed: false }, { id: uuidv4(), name: 'Chemistry', completed: false }] },
  ],
};

function RoadmapView() {
  const paperContainer = useRef(null);
  const [roadmapData, setRoadmapData] = useState(initialRoadmapData);
  const [selectedTopic, setSelectedTopic] = useState(null);

  useEffect(() => {
    renderDiagram();
  }, [roadmapData]);

  const renderDiagram = () => {
    const graph = new Graph();
    const paper = new Paper({
      el: paperContainer.current,
      model: graph,
      width: 800,
      height: 600,
      gridSize: 1,
    });

    graph.clear();

    roadmapData.subjects.forEach((subject, subjectIndex) => {
      const subjectElement = new joint.shapes.standard.Rectangle();
      subjectElement.position(100, 100 + subjectIndex * 150);
      subjectElement.resize(100, 40);
      subjectElement.attr({
        body: { fill: 'lightblue' },
        label: { text: subject.name, fill: 'black' },
      });
      subjectElement.addTo(graph);

      let currentY = 150 + subjectIndex * 150;
      subject.topics.forEach((topic) => {
        const topicElement = new joint.shapes.standard.Rectangle();
        topicElement.position(300, currentY);
        topicElement.resize(100, 40);
        topicElement.attr({
          body: { fill: topic.completed ? 'lightgreen' : 'lightcoral' },
          label: { text: topic.name, fill: 'black' },
        });

        topicElement.addTo(graph);
        topicElement.on('element:pointerclick', () => {
          setSelectedTopic(topic);
        });

        const link = new joint.shapes.standard.Link();
        link.source(subjectElement);
        link.target(topicElement);
        link.addTo(graph);

        currentY += 60; // Space between topics
      });
    });
  };

  const updateTopicName = (newName) => {
    const updatedRoadmapData = { ...roadmapData };
    updatedRoadmapData.subjects.forEach((subject) => {
      const topicToUpdate = subject.topics.find((t) => t.id === selectedTopic.id);
      if (topicToUpdate) {
        topicToUpdate.name = newName;
      }
    });

    setRoadmapData(updatedRoadmapData);
    setSelectedTopic({ ...selectedTopic, name: newName });
  };

  const markAsCompleted = () => {
    const updatedRoadmapData = { ...roadmapData };
    updatedRoadmapData.subjects.forEach((subject) => {
      const topicToUpdate = subject.topics.find((t) => t.id === selectedTopic.id);
      if (topicToUpdate) {
        topicToUpdate.completed = !topicToUpdate.completed;
      }
    });

    setRoadmapData(updatedRoadmapData);
    setSelectedTopic({ ...selectedTopic, completed: !selectedTopic.completed });
  };

  const deleteTopic = () => {
    const updatedRoadmapData = { ...roadmapData };
    updatedRoadmapData.subjects.forEach((subject) => {
      subject.topics = subject.topics.filter((t) => t.id !== selectedTopic.id);
    });

    setRoadmapData(updatedRoadmapData);
    setSelectedTopic(null);
  };

  return (
    <div style={{ display: 'flex' }}>
      <div ref={paperContainer} style={{ border: '1px solid black', width: '800px', height: '600px' }} />
      <div style={{ width: '250px', padding: '10px', marginLeft: '20px', border: '1px solid black' }}>
        <h3>Topic Details</h3>
        {selectedTopic ? (
          <>
            <h4>{selectedTopic.name}</h4>
            <label>Change Topic Name:</label>
            <input
              type="text"
              value={selectedTopic.name}
              onChange={(e) => updateTopicName(e.target.value)}
              style={{ width: '100%', marginBottom: '10px' }}
            />
            <button onClick={markAsCompleted}>
              Mark as {selectedTopic.completed ? 'Incomplete' : 'Completed'}
            </button>
            <button onClick={deleteTopic} style={{ marginTop: '10px', color: 'red' }}>
              Delete Topic
            </button>
          </>
        ) : (
          <p>Select a topic to modify its properties.</p>
        )}
      </div>
    </div>
  );
}

export default RoadmapView;
