import React, { useEffect, useState, useRef } from 'react';
import * as joint from '@joint/core';
import { v4 as uuidv4 } from 'uuid';
import './RoadmapView.css';

function RoadmapView() {
  const paperContainer = useRef(null);
  const graph = useRef(null);
  const [roadmapData, setRoadmapData] = useState(() => {
    const storedData = localStorage.getItem('roadmapData');
    return storedData ? JSON.parse(storedData) : { subjects: [] };
  });
  const [newSubject, setNewSubject] = useState('');
  const [newTopic, setNewTopic] = useState('');
  const [selectedSubjectId, setSelectedSubjectId] = useState('');
  const [file, setFile] = useState(null);

  useEffect(() => {
    graph.current = new joint.dia.Graph();
    const paper = new joint.dia.Paper({
      el: paperContainer.current,
      model: graph.current,
      width: 800,
      height: 600,
      gridSize: 1,
      interactive: { elementMove: false },
    });

    renderDiagram();
  }, [roadmapData]);

  const renderDiagram = () => {
    graph.current.clear();
    roadmapData.subjects.forEach((subject, subjectIndex) => {
      const subjectElement = new joint.shapes.standard.Rectangle();
      subjectElement.position(100 + subjectIndex * 200, 50);
      subjectElement.resize(150, 50);
      subjectElement.attr({
        body: { fill: '#007bff', stroke: '#0056b3', strokeWidth: 2 },
        label: { text: subject.name, fill: 'white', fontSize: 14 },
      });

      subjectElement.addTo(graph.current);

      let currentY = 150;
      subject.topics.forEach((topic) => {
        const topicElement = new joint.shapes.standard.Rectangle();
        topicElement.position(100 + subjectIndex * 200, currentY);
        topicElement.resize(120, 40);
        topicElement.attr({
          body: { fill: topic.completed ? '#28a745' : '#dc3545' },
          label: { text: topic.name, fill: 'white', fontSize: 12 },
        });

        topicElement.addTo(graph.current);

        const link = new joint.shapes.standard.Link();
        link.source(subjectElement);
        link.target(topicElement);
        link.addTo(graph.current);
        link.attr({ line: { stroke: '#333', strokeWidth: 2, targetMarker: { type: 'arrow', width: 10, height: 10 } } });
        link.set('router', { name: 'manhattan', args: { padding: 10 } });

        currentY += 60;
      });
    });
  };

  const addSubject = () => {
    if (newSubject.trim() !== '') {
      const updatedRoadmapData = {
        ...roadmapData,
        subjects: [
          ...roadmapData.subjects,
          {
            id: uuidv4(),
            name: newSubject,
            topics: [],
          },
        ],
      };
      setRoadmapData(updatedRoadmapData);
      setNewSubject('');
      localStorage.setItem('roadmapData', JSON.stringify(updatedRoadmapData));
    } else {
      alert('Please enter a subject name.');
    }
  };

  const addTopic = () => {
    if (newTopic.trim() !== '' && selectedSubjectId) {
      const updatedRoadmapData = { ...roadmapData };
      const subjectToUpdate = updatedRoadmapData.subjects.find((subject) => subject.id === selectedSubjectId);
      if (subjectToUpdate) {
        const topicId = uuidv4();
        subjectToUpdate.topics.push({
          id: topicId,
          name: newTopic,
          completed: false,
          attachments: [],
        });
        setRoadmapData(updatedRoadmapData);
        setNewTopic('');
        localStorage.setItem('roadmapData', JSON.stringify(updatedRoadmapData));
        renderDiagram();
      } else {
        alert('Subject not found.');
      }
    } else {
      alert('Please enter a topic name and select a subject.');
    }
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = (topicId) => {
    if (file) {
      const updatedRoadmapData = { ...roadmapData };
      updatedRoadmapData.subjects.forEach((subject) => {
        if (subject.id === selectedSubjectId) {
          const topicToUpdate = subject.topics.find((t) => t.id === topicId);
          if (topicToUpdate) {
            const newAttachment = {
              name: file.name,
              url: URL.createObjectURL(file),
            };
            topicToUpdate.attachments.push(newAttachment);
          }
        }
      });
      setRoadmapData(updatedRoadmapData);
      setFile(null);
      localStorage.setItem('roadmapData', JSON.stringify(updatedRoadmapData));
    }
  };

  const removeAttachment = (topicId, index) => {
    const updatedRoadmapData = { ...roadmapData };
    updatedRoadmapData.subjects.forEach((subject) => {
      if (subject.id === selectedSubjectId) {
        const topicToUpdate = subject.topics.find((t) => t.id === topicId);
        if (topicToUpdate) {
          topicToUpdate.attachments.splice(index, 1);
        }
      }
    });
    setRoadmapData(updatedRoadmapData);
    localStorage.setItem('roadmapData', JSON.stringify(updatedRoadmapData));
  };

  return (
    <div className="roadmap-container">
      <div className="subject-container">
        <h3>Add New Subject</h3>
        <input type="text" value={newSubject} onChange={(e) => setNewSubject(e.target.value)} placeholder="Subject Name" />
        <button className="button" onClick={addSubject}>Add Subject</button>
      </div>

      <div className="topic-container">
        <h3>Add New Topic</h3>
        <select onChange={(e) => setSelectedSubjectId(e.target.value)} value={selectedSubjectId}>
          <option value="">Select Subject</option>
          {roadmapData.subjects.map((subject) => (
            <option key={subject.id} value={subject.id}>
              {subject.name}
            </option>
          ))}
        </select>
        <input type="text" value={newTopic} onChange={(e) => setNewTopic(e.target.value)} placeholder="Topic Name" />
        <button className="button" onClick={addTopic}>Add Topic</button>
      </div>

      <div ref={paperContainer} className="diagram-container" />

      <div className="manage-container">
        <h3>Manage Topics</h3>
        {selectedSubjectId && (
          <ul>
            {roadmapData.subjects
              .find((subject) => subject.id === selectedSubjectId)
              ?.topics.map((topic, topicIndex) => (
                <li key={topic.id} className="topic-item">
                  <span>{topic.name}</span>
                  <input type="file" onChange={handleFileChange} />
                  <button onClick={() => handleUpload(topic.id)} className="button">Upload</button>
                  <div className="attachments">
                    {topic.attachments.map((attachment, index) => (
                      <div key={index} className="attachment-item">
                        <a href={attachment.url} target="_blank" rel="noopener noreferrer">{attachment.name}</a>
                        <button onClick={() => removeAttachment(topic.id, index)} className="delete-button">Delete</button>
                      </div>
                    ))}
                  </div>
                </li>
              ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default RoadmapView;
