import React, { useState, useEffect } from 'react';

const RoadmapView = () => {
  // State for subjects, topics, and files
  const [subjects, setSubjects] = useState(JSON.parse(localStorage.getItem('subjects')) || []);
  const [topics, setTopics] = useState(JSON.parse(localStorage.getItem('topics')) || []);
  const [files, setFiles] = useState(JSON.parse(localStorage.getItem('files')) || {}); // Store files per topic

  // Handle adding a subject
  const handleAddSubject = (subjectName) => {
    const newSubjects = [...subjects, subjectName];
    setSubjects(newSubjects);
    localStorage.setItem('subjects', JSON.stringify(newSubjects));
  };

  // Handle adding a topic
  const handleAddTopic = (subjectName, topicName) => {
    const newTopics = [...topics, { subject: subjectName, name: topicName }];
    setTopics(newTopics);
    localStorage.setItem('topics', JSON.stringify(newTopics));
  };

  // Handle file upload
  const handleFileUpload = (topicName, uploadedFiles) => {
    const updatedFiles = { ...files, [topicName]: [...(files[topicName] || []), ...uploadedFiles] };
    setFiles(updatedFiles);
    localStorage.setItem('files', JSON.stringify(updatedFiles));
  };

  // Handle file deletion
  const handleDeleteFile = (topicName, fileName) => {
    const updatedFiles = {
      ...files,
      [topicName]: files[topicName].filter(file => file.name !== fileName)
    };
    setFiles(updatedFiles);
    localStorage.setItem('files', JSON.stringify(updatedFiles));
  };

  return (
    <div className="roadmap-container">
      {/* Manage Topics UI */}
      <div className="manage-container">
        <h2>Manage Topics</h2>
        {topics.map((topic, index) => (
          <div key={index} className="topic-container">
            <div className="topic-item">
              <span>{topic.name}</span>
              <div className="topic-actions">
                {/* File Upload */}
                <input 
                  type="file" 
                  multiple 
                  onChange={(e) => handleFileUpload(topic.name, Array.from(e.target.files))}
                />
                <div className="file-list">
                  {files[topic.name]?.map((file, fileIndex) => (
                    <div key={fileIndex} className="file-item">
                      <span>{file.name}</span>
                      <button 
                        className="delete-button" 
                        onClick={() => handleDeleteFile(topic.name, file.name)}>
                        Delete
                      </button>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RoadmapView;
