import React, { useEffect, useState, useRef } from 'react';
import * as joint from '@joint/core';
import { v4 as uuidv4 } from 'uuid';
import { OAuth2Client } from 'google-auth-library';
import './RoadmapView.css';

const CLIENT_ID = '815091826368-983u6b63nk2bq6h588uf2k955s0bd4is.apps.googleusercontent.com';
const CLIENT_SECRET = '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';
const REDIRECT_URI = 'http://localhost:3000';

const oAuth2Client = new OAuth2Client(CLIENT_ID, CLIENT_SECRET, REDIRECT_URI);

function RoadmapView() {
  const paperContainer = useRef(null);
  const graph = useRef(null);
  const [roadmapData, setRoadmapData] = useState(() => {
    const savedData = localStorage.getItem('roadmapData');
    return savedData ? JSON.parse(savedData) : { subjects: [] };
  });
  const [newSubject, setNewSubject] = useState('');
  const [newTopic, setNewTopic] = useState('');
  const [selectedSubjectId, setSelectedSubjectId] = useState('');
  const [completionPercentage, setCompletionPercentage] = useState(0);
  const [file, setFile] = useState(null);

  useEffect(() => {
    graph.current = new joint.dia.Graph();
    const paper = new joint.dia.Paper({
      el: paperContainer.current,
      model: graph.current,
      width: 800,
      height: 600,
      gridSize: 1,
      interactive: { elementMove: false },
    });

    renderDiagram();
    calculateCompletionPercentage();

    // Check if there's an auth code in the URL
    const urlParams = new URLSearchParams(window.location.search);
    const code = urlParams.get('code');
    if (code) {
      handleAuthCallback(code);
    }

    return () => {
      graph.current.off('element:pointerdown');
    };
  }, []);

  const renderDiagram = () => {
    graph.current.clear();
    roadmapData.subjects.forEach(subject => {
      const subjectElement = new joint.shapes.standard.Rectangle();
      subjectElement.position(100, 100 + roadmapData.subjects.indexOf(subject) * 100);
      subjectElement.resize(200, 50);
      subjectElement.attr({
        body: {
          fill: '#6a6e71',
          stroke: '#fff',
          strokeWidth: 2,
        },
        label: {
          text: subject.name,
          fill: 'white',
          fontSize: 16,
        },
      });
      subjectElement.addTo(graph.current);
    });
  };

  const calculateCompletionPercentage = () => {
    const completedTopics = roadmapData.subjects.reduce((acc, subject) => {
      return acc + subject.topics.filter(topic => topic.completed).length;
    }, 0);
    const totalTopics = roadmapData.subjects.reduce((acc, subject) => {
      return acc + subject.topics.length;
    }, 0);
    setCompletionPercentage(totalTopics > 0 ? (completedTopics / totalTopics) * 100 : 0);
  };

  const addSubject = async () => {
    if (newSubject.trim() !== '') {
      const updatedRoadmapData = {
        ...roadmapData,
        subjects: [
          ...roadmapData.subjects,
          {
            id: uuidv4(),
            name: newSubject,
            topics: [],
          },
        ],
      };

      setRoadmapData(updatedRoadmapData);
      setNewSubject('');
      localStorage.setItem('roadmapData', JSON.stringify(updatedRoadmapData));

      // Fetch related topics using authenticated request
      const relatedTopics = await fetchTopicsFromGemini(newSubject);
      relatedTopics.forEach(topic => {
        addTopic(topic.name); // Assuming addTopic can add a topic by name
      });
    } else {
      alert('Please enter a subject name.');
    }
  };

  const handleAuthCallback = async (code) => {
    const { tokens } = await oAuth2Client.getToken(code);
    oAuth2Client.setCredentials(tokens);
    console.log('User authenticated and tokens set', tokens);
  };

  const fetchTopicsFromGemini = async (subject) => {
    const endpointUrl = 'https://your-gemini-api-endpoint.com'; // Replace with actual endpoint URL
    const response = await fetch(endpointUrl, {
      method: 'POST',
      headers: {
        Authorization: `Bearer ${oAuth2Client.credentials.access_token}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        subject: subject,
      }),
    });

    const data = await response.json();
    return data.topics; // Adjust according to your API response format
  };

  const refreshAccessToken = async () => {
    const { credentials } = await oAuth2Client.refreshAccessToken();
    oAuth2Client.setCredentials(credentials);
  };

  const deleteSubject = (subjectId) => {
    const updatedRoadmapData = {
      ...roadmapData,
      subjects: roadmapData.subjects.filter(subject => subject.id !== subjectId),
    };

    setRoadmapData(updatedRoadmapData);
    localStorage.setItem('roadmapData', JSON.stringify(updatedRoadmapData));
  };

  const addTopic = (topicName) => {
    const updatedRoadmapData = {
      ...roadmapData,
      subjects: roadmapData.subjects.map(subject => {
        if (subject.id === selectedSubjectId) {
          return {
            ...subject,
            topics: [
              ...subject.topics,
              {
                id: uuidv4(),
                name: topicName,
                completed: false,
              },
            ],
          };
        }
        return subject;
      }),
    };

    setRoadmapData(updatedRoadmapData);
    localStorage.setItem('roadmapData', JSON.stringify(updatedRoadmapData));
    setNewTopic('');
    calculateCompletionPercentage();
  };

  const markAsCompleted = (subjectId, topicId) => {
    const updatedRoadmapData = {
      ...roadmapData,
      subjects: roadmapData.subjects.map(subject => {
        if (subject.id === subjectId) {
          return {
            ...subject,
            topics: subject.topics.map(topic => {
              if (topic.id === topicId) {
                return { ...topic, completed: !topic.completed };
              }
              return topic;
            }),
          };
        }
        return subject;
      }),
    };

    setRoadmapData(updatedRoadmapData);
    localStorage.setItem('roadmapData', JSON.stringify(updatedRoadmapData));
    calculateCompletionPercentage();
  };

  return (
    <div className="roadmap-container">
      <div className="subject-container">
        <h3>Add New Subject</h3>
        <input
          type="text"
          value={newSubject}
          onChange={(e) => setNewSubject(e.target.value)}
          placeholder="Enter subject name"
        />
        <button onClick={addSubject}>Add Subject</button>
        <h3>Subjects</h3>
        <ul>
          {roadmapData.subjects.map((subject) => (
            <li key={subject.id}>
              {subject.name}
              <button onClick={() => deleteSubject(subject.id)}>Delete</button>
              <button onClick={() => setSelectedSubjectId(subject.id)}>Select</button>
              <ul>
                {subject.topics.map(topic => (
                  <li key={topic.id}>
                    {topic.name}
                    <button onClick={() => markAsCompleted(subject.id, topic.id)}>
                      {topic.completed ? 'Undo' : 'Complete'}
                    </button>
                  </li>
                ))}
              </ul>
            </li>
          ))}
        </ul>
      </div>
      <div ref={paperContainer} className="paper-container"></div>
      <div className="completion-percentage">
        <h3>Completion Percentage: {completionPercentage.toFixed(2)}%</h3>
      </div>
    </div>
  );
}

export default RoadmapView;
